// user.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  private apiUrl = 'http://localhost:5000/api/auth/register'; // URL de tu API backend

  constructor(private http: HttpClient) {}

  register(userData: any): Observable<any> {
    return this.http.post(this.apiUrl, userData);
  }
  updateRole(userId: string, role: string): Observable<any> {
    // Asegúrate de que este endpoint exista en tu backend
    return this.http.patch(`${this.apiUrl}/users/${userId}/role`, { role });
  }
}
