import { Component } from '@angular/core';

@Component({
  selector: 'app-artista-section',
  templateUrl: './artista-section.component.html',
  styleUrls: ['./artista-section.component.css'],
})
export class ArtistaSectionComponent {
  artists = [
    {
      image: '/assets/images/artist1.png',
      name: 'Eddy Fernando Ordoñez',
      description:
        'Artista visual especializado en murales y arte urbano con un estilo único.',
      city: 'Loja',
      contact: '123-456-789',
      email: 'eddyordoñez@example.com',
    },
    {
      image: '/assets/images/artist2.png',
      name: 'María Pérez',
      description:
        'Experta en ilustración digital y diseño gráfico contemporáneo.',
      city: 'Quito',
      contact: '234-567-890',
      email: 'mariaperez@example.com',
    },
    {
      image: '/assets/images/artist3.png',
      name: 'Carlos Jiménez',
      description:
        'Fotógrafo con enfoque en paisajes y retratos creativos.',
      city: 'Guayaquil',
      contact: '345-678-901',
      email: 'carlosjimenez@example.com',
    },
    {
      image: '/assets/images/artist4.png',
      name: 'Ana Gómez',
      description:
        'Escultora que trabaja con materiales reciclados para crear arte sostenible.',
      city: 'Cuenca',
      contact: '456-789-012',
      email: 'anagomez@example.com',
    },
  ];
}
