import { Component } from '@angular/core';

@Component({
  selector: 'app-paint-selector',
  templateUrl: './paint-selector.component.html',
  styleUrls: ['./paint-selector.component.css'],
})
export class PaintSelectorComponent {
  paints = [
    {
      title: 'Pintura 1',
      description: 'Descripción detallada de la pintura 1.',
      image: 'ruta-a-la-imagen-1.jpg',
    },
    {
      title: 'Pintura 2',
      description: 'Descripción detallada de la pintura 2.',
      image: 'ruta-a-la-imagen-2.jpg',
    },
    // Más objetos de pinturas...
  ];

  selectedPaint: any = null;

  selectPaint(paint: any) {
    this.selectedPaint = paint;
  }

  clearSelection() {
    this.selectedPaint = null;
  }

  moreDetails(paint: any) {
    // Aquí puedes manejar el botón de "Saber más", como redirigir o mostrar más detalles
    console.log('Más detalles de:', paint);
  }
}
