import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';  // Importa Router

@Component({
  selector: 'app-select-rol',
  templateUrl: './select-rol.component.html',
  styleUrls: ['./select-rol.component.css']
})
export class SelectRolComponent {
  selectRoleForm: FormGroup;
  successMessage: string | null = null;
  errorMessage: string | null = null;

  constructor(
    private fb: FormBuilder,
    private http: HttpClient,
    private router: Router // Inyecta el Router
  ) {
    this.selectRoleForm = this.fb.group({
      role: ['usuario', [Validators.required]], // Valor predeterminado 'usuario'
    });
  }
  onSubmit() {
    const role = this.selectRoleForm.controls['role'].value || 'usuario'; // Asigna 'usuario' por defecto si no se ha seleccionado nada
    const userId = localStorage.getItem('userId'); // Obtén el ID del usuario
  
    if (userId) {
      // Enviar la solicitud al servidor para actualizar el rol
      this.http
        .patch('http://localhost:5000/api/users/update-role', { userId, role })
        .subscribe(
          (response) => {
            this.successMessage = 'Rol actualizado con éxito.';
            this.errorMessage = null;
  
            // Redirigir dependiendo del rol seleccionado
            if (role === 'usuario') {
              this.router.navigate(['/home']);  // Redirige al home de usuario
            } else if (role === 'artista') {
              this.router.navigate(['/artista-view']);  // Redirige al view del artista
            }
          },
          (error) => {
            this.successMessage = null;
            this.errorMessage = 'Error al actualizar el rol: ' + error.message;
          }
        );
    } else {
      this.errorMessage = 'No se pudo encontrar el ID de usuario.';
    }
  }
}
