import { Component } from '@angular/core';
import { CartService } from '../../services/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  constructor(public cartService: CartService) {}

  closeCart(): void {
    this.cartService.toggleCart(false);
  }
}
<div
  class="fixed top-0 right-0 h-screen bg-[#d4a157] w-80 shadow-lg p-6 z-50 transition-transform"
  [ngClass]="{ 'translate-x-0': cartService.isCartOpen, 'translate-x-full': !cartService.isCartOpen }"
>
  <!-- Encabezado del carrito -->
  <div class="flex justify-between items-center mb-6">
    <h2 class="text-white text-xl font-bold">Tu Carrito</h2>
    <button (click)="closeCart()" class="text-white text-xl font-bold hover:scale-110 transition-transform">×</button>
  </div>

  <!-- Contenido -->
  <div class="flex-1 flex flex-col justify-center items-center">
    <p class="text-white text-lg mb-6">Agrega elementos para comenzar +</p>
  </div>

  <!-- Botón de acción -->
  <button
    class="bg-[#0e1f38] text-white px-4 py-2 rounded-lg shadow-md hover:scale-105 hover:bg-[#142d50] transition-transform"
  >
    Continuar a la compra
  </button>
</div>
