import { Component } from '@angular/core';

@Component({
  selector: 'app-paint-section',
  templateUrl: './paint-section.component.html',
  styleUrls: ['./paint-section.component.css'],
})
export class PaintSectionComponent {
  paints = [
    { title: 'Pintura 1', image: 'ass/to/image1.jpg', description: 'Descripción de la pintura 1.' },
    { title: 'Pintura 2', image: 'path/to/image2.jpg', description: 'Descripción de la pintura 2.' },
    // Agrega más objetos de pintura según sea necesario
  ];

  selectedPaint: any = null;

  selectPaint(paint: any) {
    this.selectedPaint = paint;
  }

  clearSelection() {
    this.selectedPaint = null;
  }

  viewMore(paint: any) {
    alert(`Más información sobre: ${paint.title}`);
  }
}
