import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  @Input() isVisible: boolean = false;

  closeCart() {
    this.isVisible = false;
  }
}
<div class="fixed inset-y-0 right-0 bg-[#f5f5dc] shadow-lg w-96 p-4 z-50 transition-transform" [class.translate-x-full]="!isVisible">
  <h2 class="text-2xl font-bold text-[#B58A44] text-center">Tu Carrito</h2>
  <p class="mt-4 text-gray-700 text-center">Agrega elementos para comenzar +</p>
  <div class="flex justify-center mt-6">
    <button
      class="bg-gradient-to-r from-[#C59955] to-[#D4A157] text-white py-2 px-6 rounded-full text-sm font-medium shadow-lg hover:from-[#B58A44] hover:to-[#C5964F] hover:scale-105 transition-transform duration-300"
      (click)="closeCart()"
    >
      Cerrar
    </button>
  </div>
</div>
