import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'],
})
export class CartComponent {
  @Input() isVisible: boolean = false;

  ingresar() {
    this.isVisible = false;
  }
}
<div class="fixed inset-0 z-50 flex justify-end" *ngIf="isVisible">
  <!-- Fondo de superposición -->
  <div 
    class="absolute inset-0 bg-black bg-opacity-50 transition-opacity"
    (click)="ingresar()"
  ></div>

  <!-- Contenedor del carrito -->
  <div class="relative bg-[#f5f5dc] shadow-lg w-96 p-4 z-50 transition-transform">
    <div class="bg-[#D6A060] bg-opacity-75 py-6 rounded-lg shadow-lg max-w-2xl mx-auto text-center mb-10">
      <h2 class="text-3xl font-semibold text-white mb-1">Tienda Online</h2>
    </div>
    <p class="mt-4 text-gray-700 text-center">Agrega elementos para comenzar</p>
    <div class="flex justify-center mt-6">
      <button
      class="mt-4 bg-[#1A3758] hover:bg-[#0E1F38] text-white py-2 px-4 rounded"
        (click)="ingresar()"
      >
        Ingresar
      </button>
    </div>
  </div>
</div>
